Level::Level(
    // const std::string& collision_file_name,
    // const bn::music_item& music_item,
    SpawnPoint player_spawn
)
    : _player_spawn(player_spawn),
    //   _collision_file_name(collision_file_name),
      level_image(bn::regular_bg_items::level_512_512_slopes.create_bg(0, 0)) {      }


void Level::load()
{
    // Load collision CSV
    collision_data = _read_collision_csv(_collision_file_name.c_str());

    // Create level image
    
    level_image = bn::regular_bg_items::level_512_512_slopes.create_bg(0, 0);
    

    // Music (disabled for now)
    // _music_item.play();
}

void Level::unload()
{
    collision_data.clear();
}

